    size_t i = 0;

    __CPROVER_assume(N > 2);
    for (size_t k = 0; k < N; k++) {
        __CPROVER_assume(arr_s[k] == 0 || arr_s[k] == '\\' || arr_s[k] == '\'' || arr_s[k] == '"' || arr_s[k] == '(' || arr_s[k] == ')' || arr_s[k] == '\n' || arr_s[k] == '|' || arr_s[k] == '&' || arr_s[k] == ';' || arr_s[k] == '<' || arr_s[k] == '>' || arr_s[k] == '{' || arr_s[k] == '}' || arr_s[k] == '$' || arr_s[k] == '`');
    }

    while (i < N && arr_s[i] != 0) {
        __CPROVER_assert(
            (sq == false || dq == false) &&
            (np >= 0) &&
            (sq == true || dq == true || np == 0 || (i > 0 && arr_s[i-1] == '(' && np > 0) || (i > 0 && arr_s[i-1] == ')' && np > 0) || (i > 1 && arr_s[i-2] == '$' && arr_s[i-1] == '(' && arr_s[i] == '(' && np >= 2)),
            "Loop invariant violated"
        );

        switch (arr_s[i]) {
            case '\\':
                if (!sq && !arr_s[++i]) return WRDE_SYNTAX;
                break;
            case '\'':
                if (!dq) sq ^= 1;
                break;
            case '"':
                if (!sq) dq ^= 1;
                break;
            case '(':
                if (np) {
                    np++;
                    break;
                }
            case ')':
                if (np) {
                    np--;
                    break;
                }
            case '\n':
            case '|':
            case '&':
            case ';':
            case '<':
            case '>':
            case '{':
            case '}':
                if (!(sq | dq | np)) return WRDE_BADCHAR;
                break;
            case '$':
                if (sq) break;
                if (arr_s[i+1]=='(' && arr_s[i+2]=='(') {
                    i += 2;
                    np += 2;
                    break;
                } else if (arr_s[i+1] != '(') break;
            case '`':
                if (sq) break;
                return WRDE_CMDSUB;
        }
